import React, { useState } from 'react';
import { SafeAreaView, StyleSheet } from 'react-native';
import Header from './components/Header';
import RecordButton from './components/RecordButton';
import PlaybackControls from './components/PlaybackControls';
import StatusMessage from './components/StatusMessage';

const App = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [message, setMessage] = useState("Ready to record");

  const startRecording = () => {
    setIsRecording(true);
    setMessage("Recording Started!");
  };

  const stopRecording = () => {
    setIsRecording(false);
    setMessage("Recording Stopped!");
  };

  const playRecording = () => setIsPlaying(true);
  const pauseRecording = () => setIsPlaying(false);
  const stopPlayback = () => setIsPlaying(false);
  const restartPlayback = () => setIsPlaying(true);

  return (
    <SafeAreaView style={styles.container}>
      <Header />
      <StatusMessage message={message} />
      {isRecording ? (
        <RecordButton onStartRecording={startRecording} onStopRecording={stopRecording} />
      ) : (
        <PlaybackControls onPlay={playRecording} onPause={pauseRecording} onStop={stopPlayback} onRestart={restartPlayback} />
      )}
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    padding: 16,
  },
});

export default App;
