import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { Audio } from 'expo-av';

export default function App() {
  const [recording, setRecording] = useState(null);
  const [sound, setSound] = useState(null);
  const [audioUri, setAudioUri] = useState(null);
  const [statusMessage, setStatusMessage] = useState("Press Record to Start!");
  const [isPlaying, setIsPlaying] = useState(false);

  const startRecording = async () => {
    try {
      await Audio.requestPermissionsAsync();
      await Audio.setAudioModeAsync({
        allowsRecordingIOS: true,
        playsInSilentModeIOS: true,
      });
      const { recording } = await Audio.Recording.createAsync(
        Audio.RECORDING_OPTIONS_PRESET_HIGH_QUALITY
      );
      setRecording(recording);
      setStatusMessage("Recording in progress...");
    } catch (err) {
      console.error('Failed to start recording', err);
    }
  };

  const stopRecording = async () => {
    try {
      if (!recording) return;
      await recording.stopAndUnloadAsync();
      const uri = recording.getURI();
      setRecording(null);
      setAudioUri(uri);
      setStatusMessage("Recording ready for playback!");
    } catch (err) {
      console.error('Failed to stop recording', err);
    }
  };

  const playRecording = async () => {
    try {
      if (sound) {
        await sound.playAsync();
        setIsPlaying(true);
      } else if (audioUri) {
        const { sound: newSound } = await Audio.Sound.createAsync(
          { uri: audioUri },
          { shouldPlay: true },
          onPlaybackStatusUpdate
        );
        setSound(newSound);
        setIsPlaying(true);
      }
    } catch (err) {
      console.error('Failed to play recording', err);
    }
  };

  const onPlaybackStatusUpdate = (status) => {
    if (status.didJustFinish) {
      setIsPlaying(false);
    }
  };

  const pausePlayback = async () => {
    try {
      if (sound) {
        await sound.pauseAsync();
        setIsPlaying(false);
      }
    } catch (err) {
      console.error('Failed to pause playback', err);
    }
  };

  const stopPlayback = async () => {
    try {
      if (sound) {
        await sound.stopAsync();
        await sound.unloadAsync();
        setSound(null);
        setIsPlaying(false);
      }
    } catch (err) {
      console.error('Failed to stop playback', err);
    }
  };

  const restartPlayback = async () => {
    try {
      if (sound) {
        await sound.replayAsync();
        setIsPlaying(true);
      } else {
        await playRecording();
      }
    } catch (err) {
      console.error('Failed to restart playback', err);
    }
  };

  React.useEffect(() => {
    return sound
      ? () => {
          sound.unloadAsync();
        }
      : undefined;
  }, [sound]);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Lab 6 - Audio Recorder App{'\n'}Your_FOL_Username</Text>
      
      <View style={styles.statusContainer}>
        <Text style={styles.statusMessage}>{statusMessage}</Text>
      </View>

      {!audioUri ? (
        <View style={styles.recordingButtonsContainer}>
          <TouchableOpacity
            style={[styles.recordButton]}
            onPress={startRecording}
            disabled={recording !== null}
          >
            <Text style={styles.recordButtonText}>Record</Text>
          </TouchableOpacity>
          
          <TouchableOpacity
            style={[styles.stopRecordingButton]}
            onPress={stopRecording}
            disabled={!recording}
          >
            <Text style={styles.stopRecordingText}>Stop Recording</Text>
          </TouchableOpacity>
        </View>
      ) : (
        <View style={styles.playbackContainer}>
          <TouchableOpacity style={styles.playbackButton} onPress={playRecording}>
            <Text style={styles.playbackButtonText}>‚ñ∂</Text>
          </TouchableOpacity>
          
          <TouchableOpacity style={styles.playbackButton} onPress={pausePlayback}>
            <Text style={styles.playbackButtonText}>‚è∏</Text>
          </TouchableOpacity>
          
          <TouchableOpacity style={styles.playbackButton} onPress={stopPlayback}>
            <Text style={styles.playbackButtonText}>‚èπ</Text>
          </TouchableOpacity>
          
          <TouchableOpacity style={styles.playbackButton} onPress={restartPlayback}>
            <Text style={styles.playbackButtonText}>üîÑ</Text>
          </TouchableOpacity>
        </View>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    paddingTop: 50,
  },
  title: {
    fontSize: 20,
    fontWeight: '400',
    textAlign: 'center',
    marginBottom: 40,
    color: '#000',
  },
  statusContainer: {
    backgroundColor: '#fff',
    padding: 15,
    borderRadius: 25,
    marginBottom: 20,
    width: '80%',
    elevation: 3,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
  },
  statusMessage: {
    fontSize: 16,
    textAlign: 'center',
    color: '#666',
  },
  recordingButtonsContainer: {
    width: '80%',
    alignItems: 'center',
    gap: 15,
  },
  recordButton: {
    backgroundColor: '#FF4444',
    paddingVertical: 12,
    paddingHorizontal: 30,
    borderRadius: 25,
    width: '50%',
    alignItems: 'center',
  },
  recordButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '500',
  },
  stopRecordingButton: {
    backgroundColor: '#9E9E9E',
    paddingVertical: 12,
    paddingHorizontal: 30,
    borderRadius: 25,
    width: '50%',
    alignItems: 'center',
  },
  stopRecordingText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '500',
  },
  playbackContainer: {
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    gap: 20,
    marginTop: 20,
  },
  playbackButton: {
    width: 50,
    height: 50,
    borderRadius: 25,
    backgroundColor: '#fff',
    justifyContent: 'center',
    alignItems: 'center',
    elevation: 3,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
  },
  playbackButtonText: {
    fontSize: 24,
    color: '#000',
  },
});
