import React, { useState } from 'react';
import { Button, View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { Audio } from 'expo-av';

export default function App() {
  const [recording, setRecording] = useState(null);
  const [sound, setSound] = useState(null);
  const [audioUri, setAudioUri] = useState(null);
  const [statusMessage, setStatusMessage] = useState("Press Record to Start!");
  const [isPlaying, setIsPlaying] = useState(false);

  // Function to start recording
  const startRecording = async () => {
    try {
      await Audio.requestPermissionsAsync();
      await Audio.setAudioModeAsync({
        allowsRecordingIOS: true,
        playsInSilentModeIOS: true,
      });
      const { recording } = await Audio.Recording.createAsync(
        Audio.RECORDING_OPTIONS_PRESET_HIGH_QUALITY
      );
      setRecording(recording);
      setStatusMessage("Recording in progress...");
    } catch (err) {
      console.error('Failed to start recording', err);
      setStatusMessage("Failed to start recording!");
    }
  };

  // Function to stop recording
  const stopRecording = async () => {
    try {
      if (!recording) return;
      
      await recording.stopAndUnloadAsync();
      const uri = recording.getURI();
      setRecording(null);
      setAudioUri(uri);
      setStatusMessage("Recording ready for playback!");
    } catch (err) {
      console.error('Failed to stop recording', err);
      setStatusMessage("Failed to stop recording!");
    }
  };

  // Function to play the recording
  const playRecording = async () => {
    try {
      if (sound) {
        await sound.playAsync();
      } else if (audioUri) {
        const { sound: newSound } = await Audio.Sound.createAsync(
          { uri: audioUri },
          { shouldPlay: true },
          onPlaybackStatusUpdate
        );
        setSound(newSound);
        setIsPlaying(true);
        setStatusMessage("Playing recording...");
      }
    } catch (err) {
      console.error('Failed to play recording', err);
      setStatusMessage("Failed to play recording!");
    }
  };

  // Playback status update handler
  const onPlaybackStatusUpdate = (status) => {
    if (status.didJustFinish) {
      setIsPlaying(false);
      setStatusMessage("Playback finished");
    }
  };

  // Function to pause playback
  const pausePlayback = async () => {
    try {
      if (sound) {
        await sound.pauseAsync();
        setIsPlaying(false);
        setStatusMessage("Playback paused");
      }
    } catch (err) {
      console.error('Failed to pause playback', err);
      setStatusMessage("Failed to pause playback!");
    }
  };

  // Function to stop playback
  const stopPlayback = async () => {
    try {
      if (sound) {
        await sound.stopAsync();
        await sound.unloadAsync();
        setSound(null);
        setIsPlaying(false);
        setStatusMessage("Playback stopped");
      }
    } catch (err) {
      console.error('Failed to stop playback', err);
      setStatusMessage("Failed to stop playback!");
    }
  };

  // Function to restart playback from the beginning
  const restartPlayback = async () => {
    try {
      if (sound) {
        await sound.replayAsync();
        setIsPlaying(true);
        setStatusMessage("Restarting playback...");
      } else {
        await playRecording();
      }
    } catch (err) {
      console.error('Failed to restart playback', err);
      setStatusMessage("Failed to restart playback!");
    }
  };

  // Cleanup function
  React.useEffect(() => {
    return sound
      ? () => {
          sound.unloadAsync();
        }
      : undefined;
  }, [sound]);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Lab 6 - Audio Recorder App{'\n'}Your_FOL_Username</Text>
      
      <View style={styles.statusContainer}>
        <Text style={styles.statusMessage}>{statusMessage}</Text>
      </View>

      <View style={styles.buttonContainer}>
        {!audioUri ? (
          <>
            <TouchableOpacity
              style={[styles.button, styles.recordButton, recording && styles.disabledButton]}
              onPress={startRecording}
              disabled={recording !== null}
            >
              <Text style={styles.buttonText}>Record</Text>
            </TouchableOpacity>
            
            <TouchableOpacity
              style={[styles.button, styles.stopButton, !recording && styles.disabledButton]}
              onPress={stopRecording}
              disabled={!recording}
            >
              <Text style={styles.buttonText}>Stop Recording</Text>
            </TouchableOpacity>
          </>
        ) : (
          <View style={styles.playbackContainer}>
            <TouchableOpacity 
              style={[styles.playbackButton, isPlaying && styles.activeButton]} 
              onPress={playRecording}
            >
              <Text style={styles.playbackButtonText}>‚ñ∂</Text>
            </TouchableOpacity>
            
            <TouchableOpacity 
              style={styles.playbackButton} 
              onPress={pausePlayback}
            >
              <Text style={styles.playbackButtonText}>‚è∏</Text>
            </TouchableOpacity>
            
            <TouchableOpacity 
              style={styles.playbackButton} 
              onPress={stopPlayback}
            >
              <Text style={styles.playbackButtonText}>‚èπ</Text>
            </TouchableOpacity>
            
            <TouchableOpacity 
              style={styles.playbackButton} 
              onPress={restartPlayback}
            >
              <Text style={styles.playbackButtonText}>üîÑ</Text>
            </TouchableOpacity>
          </View>
        )}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
    alignItems: 'center',
    paddingTop: 50,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 30,
    color: '#333',
  },
  statusContainer: {
    backgroundColor: '#fff',
    padding: 15,
    borderRadius: 10,
    marginBottom: 30,
    width: '80%',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  statusMessage: {
    fontSize: 18,
    textAlign: 'center',
    color: '#666',
  },
  buttonContainer: {
    width: '80%',
    alignItems: 'center',
  },
  button: {
    width: '60%',
    padding: 15,
    borderRadius: 25,
    marginVertical: 10,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  recordButton: {
    backgroundColor: '#ff4444',
  },
  stopButton: {
    backgroundColor: '#333',
  },
  buttonText: {
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
  },
  disabledButton: {
    opacity: 0.5,
  },
  playbackContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    width: '100%',
    padding: 20,
  },
  playbackButton: {
    width: 60,
    height: 60,
    borderRadius: 30,
    backgroundColor: '#fff',
    justifyContent: 'center',
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    margin: 10,
  },
  playbackButtonText: {
    fontSize: 24,
  },
  activeButton: {
    backgroundColor: '#e0e0e0',
  },
});
