import React, { useState } from 'react';
import { View, Button, Text, StyleSheet } from 'react-native';
import RecordAudio from './components/RecordAudio';
import PlaybackAudio from './components/PlaybackAudio';

export default function App() {
  const [isRecording, setIsRecording] = useState(false);
  const [recordedUri, setRecordedUri] = useState(null);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Audio Recorder App</Text>
      
      {!isRecording && !recordedUri && (
        <Button title="Record Sound" onPress={() => setIsRecording(true)} />
      )}
      
      {isRecording && (
        <RecordAudio onStop={(uri) => {
          setRecordedUri(uri);
          setIsRecording(false);
        }} />
      )}
      
      {recordedUri && (
        <PlaybackAudio uri={recordedUri} onRestart={() => setRecordedUri(null)} />
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, justifyContent: 'center', alignItems: 'center' },
  title: { fontSize: 24, fontWeight: 'bold', marginBottom: 20 },
});
