import React, { useState } from 'react';
import { Button, View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { Audio } from 'expo-av';

export default function App() {
  const [recording, setRecording] = useState(null);
  const [sound, setSound] = useState(null);
  const [audioUri, setAudioUri] = useState(null);
  const [statusMessage, setStatusMessage] = useState("Press Record to Start!");

  // Function to start recording
  const startRecording = async () => {
    try {
      await Audio.requestPermissionsAsync();
      await Audio.setAudioModeAsync({
        allowsRecordingIOS: true,
        playsInSilentModeIOS: true,
      });
      const { recording } = await Audio.Recording.createAsync(
        Audio.RECORDING_OPTIONS_PRESET_HIGH_QUALITY
      );
      setRecording(recording);
      setStatusMessage("Recording Started!");
    } catch (err) {
      console.error('Failed to start recording', err);
    }
  };

  // Function to stop recording
  const stopRecording = async () => {
    setRecording(null);
    await recording.stopAndUnloadAsync();
    const uri = recording.getURI();
    setAudioUri(uri);
    setStatusMessage("Recording Stopped!");
  };

  // Function to play the recording
  const playRecording = async () => {
    if (audioUri) {
      const { sound } = await Audio.Sound.createAsync({ uri: audioUri });
      setSound(sound);
      await sound.playAsync();
    }
  };

  // Function to pause playback
  const pausePlayback = async () => {
    if (sound) {
      await sound.pauseAsync();
    }
  };

  // Function to stop playback
  const stopPlayback = async () => {
    if (sound) {
      await sound.stopAsync();
      await sound.unloadAsync();
      setSound(null);
    }
  };

  // Function to restart playback from the beginning
  const restartPlayback = async () => {
    if (sound) {
      await sound.replayAsync();
    } else {
      playRecording();
    }
  };

  return (
    <View style={styles.container}>
    <div class="topDiv">
      <Text style={styles.title}>Lab 6 - Audio App</Text>
      <Text style={styles.statusMessage}>{statusMessage}</Text>
    </div>
    <div class="midDiv">
      {!audioUri ? (
        <View style={styles.buttonContainer}>
          <TouchableOpacity
            style={[styles.button, styles.recordButton]}
            onPress={startRecording}
            disabled={recording !== null}
          >
            <Text style={styles.buttonText}>Record</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={[styles.button, styles.stopButton]}
            onPress={stopRecording}
            disabled={!recording}
          >
            <Text style={styles.buttonText}>Stop</Text>
          </TouchableOpacity>
        </View>
      ) : (
        <View style={styles.buttonContainer}>
          <TouchableOpacity style={styles.iconButton} onPress={playRecording}>
            <Text style={styles.iconText}>‚ñ∂</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.iconButton} onPress={pausePlayback}>
            <Text style={styles.iconText}>‚è∏</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.iconButton} onPress={stopPlayback}>
            <Text style={styles.iconText}>‚èπ</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.iconButton} onPress={restartPlayback}>
            <Text style={styles.iconText}>üîÑ</Text>
          </TouchableOpacity>
        </View>
      )}
      </div>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  statusMessage: {
    fontSize: 18,
    color: 'red',
    marginVertical: 10,
    backgroundColor: 'bisque',
    padding: '1pc',
    borderRadius: '1pc',
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    width: '60%',
    marginTop: 20,
  },
  button: {
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
  },
  recordButton: {
    backgroundColor: '#f44336',
  },
  stopButton: {
    backgroundColor: '#000',
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
  },
  iconButton: {
    padding: 10,
    marginHorizontal: 5,
  },
  iconText: {
    fontSize: 24,
  },
  topDiv: {
    
  }
});
